mapper.select:
SELECT COUNT(*) FROM BOARD
WHERE BOARD_TYPE = #{boardType}
AND BOARD_ST = 'N'

SELECT COUNT(*) FROM
"LIKE"
WHERE LIKE_TARGET_NO = #{boardNo}
AND USER_NO = #{userNo}

mapper.delete:
DELETE FROM "LIKE"
WHERE LIKE_TARGET_NO = #{boardNo}
AND USER_NO = #{userNo}

mapper.insert:
SELECT SEQ_BOARD_NO.NEXTVAL FROM DUAL
INSERT INTO BOARD ( BOARD_NO, USER_NO, BOARD_TYPE, CHALLENGE_NO, BOARD_TITLE, BOARD_CONTENT, BOARD_CNT, BOARD_ST, CREATE_DT, UPDATE_DT, BOARD_IMG ) VALUES ( #{boardNo}, #{userNo}, #{boardType}, null, #{boardTitle}, #{boardContent}, #{boardCnt}, 'N', DEFAULT, DEFAULT, #{boardImg}
)

mapper.update:
UPDATE BOARD
SET BOARD_IMG = #{boardImg}
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD SET BOARD_TITLE =#{boardTitle},
BOARD_CONTENT=#{boardContent},
UPDATE_DT=SYSDATE
WHERE BOARD_TYPE=#{boardType}
AND BOARD_NO = #{boardNo}

        UPDATE BOARD
        SET BOARD_TITLE = #{boardTitle},
            BOARD_CONTENT = #{boardContent},
            UPDATE_DT=SYSDATE
       WHERE BOARD_TYPE=#{boardType}
		 AND BOARD_NO = #{boardNo}

UPDATE Board
SET BOARD_IMG = #{boardImg}
WHERE BOARD_NO = #{boardNo}
AND BOARD_IMG = #{deleteImagePath}

UPDATE Board
SET BOARD_IMG = #{boardImg}
WHERE BOARD_NO = #{boardNo}

UPDATE DIARY_FOOD
SET SERVING_SIZE = #{servingSize}, 
CALORIES_INTAKE = #{totalCalories}
WHERE BOARD_NO = #{boardNo} 
AND FOOD_NO = #{foodNo}

UPDATE DIARY_WORKOUT 
SET WORKOUT_DURATION = #{duration}, CALORIES_BURNED = #{caloriesBurned}
WHERE BOARD_NO = #{boardNo} AND WORKOUT_NO = #{workoutNo}
